import React from 'react';
import Modal from 'react-bootstrap/Modal';
import Button from 'react-bootstrap/Button';
import ReactDOM from "react-dom";
import 'Modal-css.css'

export default function ModalDelete(props) {

  function getName(){
    if (!props.student) return "";
    return `${props.student.firstName} ${props.student.lastName} `;
  }
    return (
			<Modal className="modal-main"
				show={props.showDeleteModal}
				centeredaria-labelledby="modal-title">
				<Modal.Header>
					<Modal.Title id="modal-title">Delete Student
					</Modal.Title>
				</Modal.Header>
				<Modal.Body>
					<h4>Are you sure?</h4>
					<p>
					  The information for student
						{getName()}
					  will be deleted and cannot be recovered!
					</p>
				</Modal.Body>
				<Modal.Footer>
						<Button
							onClick={props.deleteConfirmed}
						>Delete</Button>
						<Button
							onClick={props.hideModal}
						>Cancel</Button>
				</Modal.Footer>
			</Modal>


    );
	const container = document.createElement("div");
	document.body.appendChild(container);
	ReactDOM.render(<ModalDelete />, container);
}
